import androidx.lifecycle.flowWithLifecycle
import androidx.navigation.NavController
import com.duongpt.expensetracker.R
import com.duongpt.expensetracker.ui.theme.AppColor
import com.duongpt.expensetracker.features.money.ui.budgets.components.BudgetContent
import com.duongpt.expensetracker.features.money.ui.budgets.components.EmptyBudgetContent
import com.duongpt.expensetracker.features.money.ui.navigation.screen.Screen
import kotlinx.coroutines.flow.collectLatest

/**
 * Budget Screen UI
 */
@Composable
fun BudgetScreen(
 modifier: Modifier = Modifier,
 viewModel: BudgetViewModel = hiltViewModel(),
 navController: NavController,
 onCreateBudgetClick: () -> Unit = {},
 onHowToUseClick: () -> Unit = {}
) {
 // Collect view state
 val viewState by viewModel.viewState.collectAsState()

 // Handle wallet selection from ChooseWalletScreen
 LaunchedEffect(navController) {
 val savedStateHandle = navController.currentBackStackEntry?.savedStateHandle

 // Get wallet selection from savedStateHandle
 savedStateHandle?.get<Int>("selected_wallet_id")?.let { walletId ->
 val isTotalWallet = savedStateHandle.get<Boolean>("is_total_wallet") ?: false

 // Process the selected wallet
 viewModel.processIntent(BudgetScreenIntent.ChangeWallet(walletId, isTotalWallet))

 // Clear the saved state to avoid processing it again if we return to this screen
 savedStateHandle.remove<Int>("selected_wallet_id")
 savedStateHandle.remove<Boolean>("is_total_wallet")
 }
 }

 // Handle events
 val lifecycleOwner = LocalLifecycleOwner.current
 LaunchedEffect(key1 = viewModel, key2 = lifecycleOwner) {
 viewModel.event
 .flowWithLifecycle(lifecycleOwner.lifecycle, Lifecycle.State.STARTED)
 .collectLatest { event ->
 when (event) {
 is BudgetScreenEvent.NavigateToCreateBudget -> {
 navController.navigate(Screen.AddBudget.route)
 }
 is BudgetScreenEvent.ShowError -> {
 // Show error toast or snackbar
 }
 is BudgetScreenEvent.WalletChanged -> {
 // Handle wallet changed event if needed
 }
 is BudgetScreenEvent.BudgetCreated -> {
 // Handle budget created event if needed
 }
 is BudgetScreenEvent.BudgetDeleted -> {
 // Handle budget deleted event if needed
 }
 null -> { /* Ignore null events */ }
 }
 }
 }

 // Initial data load
 LaunchedEffect(key1 = Unit) {
 viewModel.processIntent(BudgetScreenIntent.LoadBudgets)
 }

 // Render the screen
 BudgetScreenContent(
 state = viewState,
 onWalletSelectorClick = {
 // Navigate to ChooseWalletScreen using the route helper
 navController.navigate(Screen.ChooseWallet.createRoute())
 },
 onCreateBudgetClick = {
 viewModel.processIntent(BudgetScreenIntent.CreateBudget)
 },
 onRefreshBudgets = {
 viewModel.processIntent(BudgetScreenIntent.RefreshBudgets)
 },
 onDeleteBudget = { budgetId ->
 viewModel.processIntent(BudgetScreenIntent.DeleteBudget(budgetId))
 },
 onHowToUseClick = onHowToUseClick
 )
}

/**
 * Budget Screen Content
 */
@Composable
fun BudgetScreenContent(
 modifier: Modifier = Modifier,
 state: BudgetScreenState,
 onWalletSelectorClick: () -> Unit,
 onCreateBudgetClick: () -> Unit,
 onRefreshBudgets: () -> Unit,
 onDeleteBudget: (Int) -> Unit,
 onHowToUseClick: () -> Unit
) {
 Scaffold(
 topBar = {
 TopAppBar(
 title = { 
 Column {
 Text("Running Budgets", color = Color.White)

 // Display current wallet name
 state.currentWallet?.let { wallet ->
 Text(
 text = if (state.isTotalWallet) "All Wallets" else wallet.walletName,
 color = Color.White.copy(alpha = 0.7f),
 fontSize = 12.sp
 )
 }
 }
 },
 colors = TopAppBarDefaults.topAppBarColors(
 containerColor = AppColor.Dark.PrimaryColor.containerColor,
 titleContentColor = AppColor.Dark.PrimaryColor.contentColor,
 ),
 actions = {
 Box(modifier = Modifier.clickable {
 onWalletSelectorClick()
 }) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Center
 ) {
 Icon(
 imageVector = Icons.Filled.Info,
 contentDescription = "Wallet",
 tint = Color.White,
 modifier = Modifier.size(32.dp)
 )

 Column {
 Icon(
 imageVector = Icons.Filled.KeyboardArrowUp,
 contentDescription = "Select wallet up",
 tint = Color.White,
 modifier = Modifier.size(24.dp).offset(y = 8.dp),
 )

 Icon(
 imageVector = Icons.Filled.KeyboardArrowDown,
 contentDescription = "Select wallet down",
 tint = Color.White,
 modifier = Modifier.size(24.dp).offset(y = -8.dp)
 )
 }
 }
 }

 IconButton(onClick = { onRefreshBudgets() }) {
 Icon(
 painter = painterResource(id = R.drawable.ic_more),
 contentDescription = "More options",
 tint = Color.White
 )
 }
 }
 )
 },
 containerColor = AppColor.Dark.PrimaryColor.containerColor,
 contentColor = AppColor.Dark.PrimaryColor.contentColor
 ) { paddingValues ->
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 ) {
 // Loading indicator
 if (state.isLoading) {
 CircularProgressIndicator(
 modifier = Modifier.align(Alignment.Center),
 color = Color.White
 )
 } else if (state.isBudgetEmpty) {
 // Empty state UI
 EmptyBudgetContent(
 paddingValues = PaddingValues(),
 onCreateBudgetClick = onCreateBudgetClick,
 onHowToUseClick = onHowToUseClick
 )
 } else {
 // Budget content UI
 BudgetContent(
 paddingValues = PaddingValues(),
 onCreateBudgetClick = onCreateBudgetClick,
 transactions = state.transactions,
 totalBudgetAmount = state.totalBudgetAmount,
 totalSpentAmount = state.totalSpentAmount,
 currentWallet = state.currentWallet,
 onDeleteBudget = onDeleteBudget
 )
 }

 // Error message if needed
 state.error?.let { errorMessage ->
 Text(
 text = errorMessage,
 color = Color.Red,
 modifier = Modifier
 .align(Alignment.BottomCenter)
 .padding(16.dp)
 .background(Color.White.copy(alpha = 0.8f), shape = androidx.compose.foundation.shape.RoundedCornerShape(8.dp))
 .padding(8.dp)
 )
 }
 }
 }
}

