app/src/main/java/com/duongpt/expensetracker/features/money/ui/budgets/BudgetViewModel.kt
```kotlin:app/src/main/java/com/duongpt/expensetracker/features/money/ui/budgets/BudgetViewModel.kt
package com.duongpt.expensetracker.features.money.ui.budgets

import android.util.Log
import androidx.lifecycle.viewModelScope
import com.duongpt.expensetracker.core.failure.Failure
import com.duongpt.expensetracker.core.functional.Either
import com.duongpt.expensetracker.core.interactor.UseCase
import com.duongpt.expensetracker.core.platform.BaseViewModel
import com.duongpt.expensetracker.features.money.domain.model.Transaction
import com.duongpt.expensetracker.features.money.domain.model.TransactionType
import com.duongpt.expensetracker.features.money.domain.model.WalletWithCurrency
import com.duongpt.expensetracker.features.money.domain.usecases.GetDefaultWalletUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.GetTransactionsByCategoryAndWalletUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.GetWalletsUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.ObserveTransactionsUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.ObserveWalletByIdUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.SaveBudgetUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.GetBudgetsByWalletUseCase
import com.duongpt.expensetracker.features.money.ui.transactions.DailyTransactions
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class BudgetViewModel @Inject constructor(
 private val getWalletsUseCase: GetWalletsUseCase,
 private val getDefaultWalletUseCase: GetDefaultWalletUseCase,
 private val observeWalletByIdUseCase: ObserveWalletByIdUseCase,
 private val getTransactionsByCategoryAndWalletUseCase: GetTransactionsByCategoryAndWalletUseCase,
 private val observeTransactionsUseCase: ObserveTransactionsUseCase,
 private val saveBudgetUseCase: SaveBudgetUseCase,
 private val getBudgetsByWalletUseCase: GetBudgetsByWalletUseCase
) : BaseViewModel<BudgetScreenState, BudgetScreenIntent, BudgetScreenEvent>() {

 override val _viewState = MutableStateFlow(BudgetScreenState())

 init {
 loadInitialData()
 }

 private fun loadInitialData() {
 _viewState.value = _viewState.value.copy(isLoading = true)

 getWalletsUseCase(UseCase.None()) { result ->
 result.fold(
 { failure ->
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = failure.toString()
 )
 emitEvent(BudgetScreenEvent.ShowError(failure.toString()))
 },
 { walletsFlow ->
 viewModelScope.launch {
 try {
 val wallets = walletsFlow.first()

 if (wallets.isNotEmpty()) {

 getDefaultWalletUseCase(UseCase.None()) { defaultResult ->
 defaultResult.fold(
 { failure ->
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = failure.toString()
 )
 emitEvent(BudgetScreenEvent.ShowError(failure.toString()))
 },
 { defaultWalletFlow ->
 viewModelScope.launch {
 try {
 val defaultWalletEntity =
 defaultWalletFlow.first()
 val defaultWallet = defaultWalletEntity!!

 _viewState.value = _viewState.value.copy(
 wallets = wallets,
 currentWallet = defaultWallet,
 isLoading = false,
 isBudgetEmpty = false
 )
 Log.i("BudgetViewModel", "loadInitialData: defaultWallet: $wallets")

 defaultWallet?.let {
 loadTransactionsForWallet(it.id, false)
 }
 } catch (e: Exception) {
 handleError(e)
 }
 }
 }
 )
 }
 } else {
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 isBudgetEmpty = true
 )
 }
 } catch (e: Exception) {
 handleError(e)
 }
 }
 }
 )
 }
 }

 private fun loadTransactionsForWallet(walletId: Int, isTotalWallet: Boolean) {
 _viewState.value = _viewState.value.copy(isLoading = true, isTotalWallet = isTotalWallet)

 if (isTotalWallet) {
 loadAllTransactions()
 } else {
 loadWalletTransactions(walletId)
 }
 }

 private fun loadWalletTransactions(walletId: Int) {
 observeWalletByIdUseCase(ObserveWalletByIdUseCase.Params.ByWalletId(walletId)) { walletResult ->
 walletResult.fold(
 { failure -> handleError(failure.toString()) },
 { walletFlow ->
 getTransactionsByCategoryAndWalletUseCase(
 GetTransactionsByCategoryAndWalletUseCase.Params(walletId = walletId)
 ) { transactionsResult ->
 transactionsResult.fold(
 { failure -> handleError(failure.toString()) },
 { transactionsFlow ->
 Log.i("BudgetViewModel", "loadWalletTransactions: ")

 getBudgetsByWalletUseCase(
 GetBudgetsByWalletUseCase.Params.ByWalletId(walletId)
 ) { budgetsResult ->
 budgetsResult.fold(
 { failure -> handleError(failure.toString()) },
 { budgetsFlow ->
 viewModelScope.launch {
 try {
 val wallet = walletFlow.first()
 val budgets = budgetsFlow.first()

 transactionsFlow.collectLatest { transactions ->
 // Get actual budget amount or use default
 val totalBudgetAmount =
 budgets.sumOf { budget -> budget.amount }
 val totalSpentAmount = transactions
 .filter { it.transactionType == TransactionType.OUTFLOW }
 .sumOf { it.amount }

 _viewState.value = _viewState.value.copy(
 currentWallet = wallet,
 transactions = transactions,
 totalBudgetAmount = if (totalBudgetAmount > 0) totalBudgetAmount else 0.0,
 totalSpentAmount = totalSpentAmount,
 isBudgetEmpty = budgets.isEmpty(),
 isLoading = false
 )
 }
 } catch (e: Exception) {
 handleError(e)
 }
 }
 }
 )
 }
 }
 )
 }
 }
 )
 }
 }

 private fun loadAllTransactions() {
 getWalletsUseCase(UseCase.None()) { result ->
 result.fold(
 { failure -> handleError(failure.toString()) },
 { walletsFlow ->
 viewModelScope.launch {
 try {
 val wallets = walletsFlow.first()

 val totalBalance = wallets.sumOf { it.currentBalance }
 val mainCurrency = wallets.firstOrNull { it.isMainWallet }?.currency
 ?: wallets.firstOrNull()?.currency

 if (mainCurrency != null) {
 val totalWallet = wallets.firstOrNull()?.copy(
 id = -1,
 walletName = "All Wallets",
 currentBalance = totalBalance,
 currency = mainCurrency,
 isMainWallet = true
 )

 if (totalWallet != null) {
 observeTransactionsUseCase(ObserveTransactionsUseCase.Params.AllWallets) { result ->
 result.fold(
 { failure -> handleError(failure.toString()) },
 { dailyTransactionsFlow ->
 getBudgetsByWalletUseCase(
 GetBudgetsByWalletUseCase.Params.AllWallets
 ) { budgetsResult ->
 budgetsResult.fold(
 { failure -> handleError(failure.toString()) },
 { budgetsFlow ->
 viewModelScope.launch {
 try {
 val budgets = budgetsFlow.first()
 val totalBudgetAmount =
 budgets.sumOf { budget -> budget.amount }

 dailyTransactionsFlow.collectLatest { dailyTransactions ->
 val allTransactions = dailyTransactions.flatMap { it.transactions }
 val totalSpentAmount = allTransactions
 .filter { it.transactionType == TransactionType.OUTFLOW }
 .sumOf { it.amount }

 _viewState.value = _viewState.value.copy(
 currentWallet = totalWallet,
 transactions = allTransactions,
 totalBudgetAmount = if (totalBudgetAmount > 0) totalBudgetAmount else 0.0,
 totalSpentAmount = totalSpentAmount,
 isBudgetEmpty = budgets.isEmpty(),
 isLoading = false
 )
 }
 } catch (e: Exception) {
 handleError(e)
 }
 }
 }
 )
 }
 }
 )
 }
 } else {
 handleError("Could not create total wallet")
 }
 } else {
 handleError("No currency available")
 }
 } catch (e: Exception) {
 handleError(e)
 }
 }
 }
 )
 }
 }

 private fun handleError(e: Exception) {
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = e.message
 )
 emitEvent(BudgetScreenEvent.ShowError(e.message ?: "An error occurred"))
 }

 private fun handleError(message: String) {
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = message
 )
 emitEvent(BudgetScreenEvent.ShowError(message))
 }

 override fun processIntent(intent: BudgetScreenIntent) {
 when (intent) {
 is BudgetScreenIntent.LoadBudgets -> loadInitialData()

 is BudgetScreenIntent.ChangeWallet -> {
 if (intent.isTotalWallet) {
 loadTransactionsForWallet(intent.walletId, true)
 } else {
 observeWalletByIdUseCase(ObserveWalletByIdUseCase.Params.ByWalletId(intent.walletId)) { result ->
 result.fold(
 { failure -> handleError(failure.toString()) },
 { walletFlow ->
 viewModelScope.launch {
 try {
 val wallet = walletFlow.first()
 _viewState.value = _viewState.value.copy(
 currentWallet = wallet
 )
 loadTransactionsForWallet(wallet.id, false)
 emitEvent(BudgetScreenEvent.WalletChanged(wallet, false))
 } catch (e: Exception) {
 handleError(e)
 }
 }
 }
 )
 }
 }
 }

 is BudgetScreenIntent.CreateBudget -> {
 emitEvent(BudgetScreenEvent.NavigateToCreateBudget)
 }

 is BudgetScreenIntent.DeleteBudget -> {
 _viewState.value = _viewState.value.copy(isLoading = true)
 emitEvent(BudgetScreenEvent.BudgetDeleted)
 loadInitialData()
 }

 is BudgetScreenIntent.RefreshBudgets -> {
 loadInitialData()
 }
 }
 }
}
```
