// 3. Main App Navigation Composable
@RequiresApi(Build.VERSION_CODES.O)
@Composable
fun AppNavigation(shouldNavigateToAddTransaction: MutableState<Boolean>) {
 val navController = rememberNavController()
 val currentBackStackEntry by navController.currentBackStackEntryAsState()
 val currentRoute = currentBackStackEntry?.destination?.route

 // Check if we're on a full-screen page that should hide bottom nav
 val isFullScreenPage = currentRoute == Screen.AddTransaction.route ||
 currentRoute?.startsWith("detail_transaction") == true ||
 currentRoute?.startsWith("edit_transaction") == true ||
 currentRoute?.startsWith("enter_amount") == true ||
 currentRoute == Screen.TransferMoney.route ||
 currentRoute == Screen.MyWallets.route ||
 currentRoute == Screen.AddWallet.route ||
 currentRoute == Screen.Currency.route ||
 currentRoute == Screen.Category.route ||
 currentRoute == Screen.AddBudget.route ||
 currentRoute?.startsWith("choose_wallet") == true

 LaunchedEffect(shouldNavigateToAddTransaction.value) {
 if (shouldNavigateToAddTransaction.value) {
 navController.navigate(Screen.AddTransaction.route)
 shouldNavigateToAddTransaction.value = false
 }
 }

 Scaffold(
 bottomBar = {
 // Only show the bottom bar when not on full-screen pages
 AnimatedVisibility(
 visible = !isFullScreenPage,
 enter = slideInVertically(initialOffsetY = { it }),
 exit = slideOutVertically(targetOffsetY = { it })
 ) {
 NavigationBar {
 val currentDestination = currentBackStackEntry?.destination

 // Navigation items before center Add button
 bottomNavItems.take(2).forEach { screen ->
 AppBottomNavigationItem(
 screen = screen,
 currentDestination = currentDestination,
 navController = navController
 )
 }

 // Center Add button
 Box(
 modifier = Modifier.padding(10.dp),
 contentAlignment = Alignment.Center
 ) {
 IconButton(
 onClick = { navController.navigate(Screen.AddTransaction.route) },
 modifier = Modifier
 .background(
 color = MaterialTheme.colorScheme.primary,
 shape = CircleShape
 )
 .padding(2.dp)
 ) {
 Icon(
 Icons.Filled.Add,
 contentDescription = "Add Expense",
 tint = MaterialTheme.colorScheme.onPrimary
 )
 }
 }

 // Navigation items after center Add button
 bottomNavItems.drop(2).forEach { screen ->
 AppBottomNavigationItem(
 screen = screen,
 currentDestination = currentDestination,
 navController = navController
 )
 }
 }
 }
 }
 ) { innerPadding ->
 // Apply padding only when not on full-screen pages
 val contentModifier = if (isFullScreenPage) {
 Modifier.fillMaxSize()
 } else {
 Modifier.padding(horizontal = innerPadding.calculateLeftPadding(LayoutDirection.Ltr))
 }

 NavHost(
 navController = navController,
 startDestination = Screen.Home.route,
 modifier = contentModifier
 ) {
 composable(Screen.Home.route) { HomeScreen(modifier = Modifier, navController = navController) }
 composable(Screen.Transactions.route) { TransactionScreen(navController = navController) }
 composable(Screen.Budget.route) { 
 BudgetScreen(
 modifier = Modifier,
 navController = navController,
 onCreateBudgetClick = { navController.navigate(Screen.AddBudget.route) },
 onHowToUseClick = { /* Handle how to use click */ }
 ) 
 }
 composable(Screen.Account.route) { AccountScreen(modifier = Modifier) }
 composable(Screen.AddTransaction.route) {
 AddTransactionScreen(
 onCloseClick = { navController.popBackStack() },
 navController = navController
 )
 }
 composable(Screen.Category.route) {
 CategoryScreen(
 onBackClick = { navController.popBackStack() },
 onCategorySelected = { selectedCategory ->
 // Always set the selected category in the previous back stack entry
 navController.previousBackStackEntry?.savedStateHandle?.set(
 "selected_category",
 selectedCategory
 )
 navController.popBackStack()
 }
 )
 }
 composable(Screen.MyWallets.route) {
 MyWalletsScreen(
 onNavigateBack = { navController.popBackStack() },
 onNavigateToAddWallet = { navController.navigate(Screen.AddWallet.route) },
 onNavigateToWalletDetail = { walletId -> /* Will be implemented later */ }
 )
 }
 composable(Screen.AddWallet.route) {
 AddWalletScreen(
