package com.duongpt.expensetracker.features.money.domain.model

import com.duongpt.expensetracker.features.money.data.data_source.local.model.BudgetEntity
import java.util.Date

data class Budget(
 val budgetId: Int = 0,
 val category: Category,
 val wallet: WalletWithCurrency,
 val amount: Double,
 val fromDate: Date,
 val endDate: Date
) {
 fun toBudgetEntity(): BudgetEntity {
 return BudgetEntity(
 budgetId = budgetId,
 categoryId = category.id,
 walletId = wallet.id,
 amount = amount,
 fromDate = fromDate,
 endDate = endDate
 )
 }

 companion object {
 fun fromEntity(
 entity: BudgetEntity,
 category: Category,
 wallet: WalletWithCurrency
 ): Budget {
 return Budget(
 budgetId = entity.budgetId,
 category = category,
 wallet = wallet,
 amount = entity.amount,
 fromDate = entity.fromDate,
 endDate = entity.endDate
 )
 }
 }
}


