// ... existing code ...

private fun loadInitialData() {
 _viewState.value = _viewState.value.copy(isLoading = true)

 // Get wallets
 getWalletsUseCase(UseCase.None()) { walletsResult ->
 walletsResult.fold(
 { failure ->
 // Handle failure
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = failure.toString()
 )
 emitEvent(BudgetScreenEvent.ShowError(failure.toString()))
 },
 { walletsFlow ->
 // Subscribe to the wallets Flow
 viewModelScope.launch {
 try {
 val wallets = walletsFlow.first()
 if (wallets.isNotEmpty()) {
 // Get default wallet
 getDefaultWalletUseCase(UseCase.None()) { defaultWalletResult ->
 defaultWalletResult.fold(
 { failure ->
 // Handle failure
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = failure.toString()
 )
 emitEvent(BudgetScreenEvent.ShowError(failure.toString()))
 },
 { defaultWalletFlow ->
 // Subscribe to default wallet flow
 viewModelScope.launch {
 try {
 val defaultWallet = defaultWalletFlow.first()
 // Update view state with wallets
 val domainWallets = wallets.map { WalletWithCurrency.fromEntity(it) }
 _viewState.value = _viewState.value.copy(
 wallets = domainWallets,
 currentWallet = defaultWallet?.let { WalletWithCurrency.fromEntity(it) },
 isLoading = false
 )

 // Load transactions for default wallet
 if (defaultWallet != null) {
 loadTransactionsForWallet(defaultWallet.wallet.id, false)
 }
 } catch (e: Exception) {
 _viewState.value = _viewState.value.copy(
 isLoading = false, 
 error = e.message
 )
 emitEvent(BudgetScreenEvent.ShowError(e.message ?: "An error occurred"))
 }
 }
 }
 )
 }
 } else {
 // No wallets available
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 isBudgetEmpty = true
 )
 }
 } catch (e: Exception) {
 _viewState.value = _viewState.value.copy(
 isLoading = false, 
 error = e.message
 )
 emitEvent(BudgetScreenEvent.ShowError(e.message ?: "An error occurred"))
 }
 }
 }
 )
 }
