class ObserveWalletByIdUseCase(
 private val walletRepository: WalletRepository
) : UseCase<Flow<WalletWithCurrency>, ObserveWalletByIdUseCase.Params>() {

 override suspend fun run(params: Params): Either<Failure, Flow<WalletWithCurrency>> {
 return try {
 val walletId = when (params) {
 is Params.ByWalletId -> params.walletId
 }
