```kotlin:app/src/main/java/com/duongpt/expensetracker/features/money/ui/addtransaction/AddTransactionViewModel.kt
package com.duongpt.expensetracker.features.money.ui.addtransaction

import android.util.Log
import androidx.lifecycle.viewModelScope
import com.duongpt.expensetracker.core.platform.BaseViewModel
import com.duongpt.expensetracker.features.money.domain.model.TransactionType
import com.duongpt.expensetracker.features.money.data.data_source.local.model.WalletWithCurrencyEntity
import com.duongpt.expensetracker.features.money.ui.addtransaction.components.NumpadButton
import com.duongpt.expensetracker.features.money.domain.model.Category
import com.duongpt.expensetracker.features.money.domain.model.CategoryType
import com.duongpt.expensetracker.features.money.domain.model.Currency
import com.duongpt.expensetracker.features.money.domain.model.WalletWithCurrency
import com.duongpt.expensetracker.features.money.domain.usecases.GetWalletsUseCase
import com.duongpt.expensetracker.features.money.domain.usecases.SaveTransactionUseCase
import com.duongpt.expensetracker.core.interactor.UseCase
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.launch
import java.util.Date
import javax.inject.Inject
import kotlinx.coroutines.flow.onEach
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.flow.launchIn

@HiltViewModel
class AddTransactionViewModel @Inject constructor(
 private val getWalletsUseCase: GetWalletsUseCase,
 private val saveTransactionUseCase: SaveTransactionUseCase
) : BaseViewModel<AddTransactionState, AddTransactionIntent, AddTransactionEvent>() {

 override val _viewState = MutableStateFlow(AddTransactionState())

 init {
 loadWallets()
 }

 private fun loadWallets() {
 getWalletsUseCase(
 params = UseCase.None(),
 scope = viewModelScope
 ) { result ->
 result.fold(
 { failure ->
 // Handle error
 emitEvent(AddTransactionEvent.ShowError("Failed to load wallets"))
 },
 { walletsFlow ->
 walletsFlow
 .onEach { wallets ->
 if (wallets.isNotEmpty()) {
 val firstWallet = wallets.first()
 _viewState.value = _viewState.value.copy(
 walletWithCurrency = firstWallet,
 isValidWallet = true
 )
 }
 }
 .catch { e ->
 emitEvent(AddTransactionEvent.ShowError("Failed to load wallets: ${e.message}"))
 }
 .launchIn(viewModelScope)
 }
 )
 }
 }

 override fun processIntent(intent: AddTransactionIntent) {
 when (intent) {
 is AddTransactionIntent.UpdateAmount -> updateAmount(intent.amount)
 is AddTransactionIntent.SelectWallet -> selectWallet(intent.walletWithCurrency)
 is AddTransactionIntent.SelectCategory -> selectCategory(intent.category)
 is AddTransactionIntent.UpdateDescription -> updateDescription(intent.description)
 is AddTransactionIntent.SelectTransactionType -> selectTransactionType(intent.type)
 is AddTransactionIntent.SelectDate -> selectDate(intent.date)
 is AddTransactionIntent.NumpadPressed -> handleNumpadPress(intent.button)
 is AddTransactionIntent.ToggleMoreDetails -> toggleMoreDetails()
 is AddTransactionIntent.ShowCategorySelector -> showCategorySelector()
 is AddTransactionIntent.ShowWalletSelector -> showWalletSelector()
 is AddTransactionIntent.ShowDatePicker -> showDatePicker()
 is AddTransactionIntent.Save -> saveTransaction()
 is AddTransactionIntent.UpdateWithPerson -> updateWithPerson(intent.person)
 is AddTransactionIntent.UpdateEventName -> updateEventName(intent.event)
 is AddTransactionIntent.SetReminder -> setReminder(intent.hasReminder)
 is AddTransactionIntent.UpdateExcludeFromReport -> updateExcludeFromReport(intent.exclude)
 is AddTransactionIntent.UpdatePhotoUri -> updatePhotoUri(intent.uri)
 }
 }

 private fun updateAmount(amount: String) {
 _viewState.value = _viewState.value.copy(
 amount = amount,
 isValidAmount = amount.toDoubleOrNull() != null && amount.toDoubleOrNull() != 0.0
 )
 }

 private fun selectWallet(walletWithCurrency: WalletWithCurrencyEntity) {
 _viewState.value = _viewState.value.copy(
 walletWithCurrency = walletWithCurrency,
 isValidWallet = true,
 showWallets = false
 )
 }

 private fun selectCategory(category: Category) {
 _viewState.value = _viewState.value.copy(
 category = category,
 isValidCategory = category != null,
 showCategories = false
 )
 }

 private fun updateDescription(description: String) {
 _viewState.value = _viewState.value.copy(description = description)
 }

 private fun selectTransactionType(type: TransactionType) {
 _viewState.value = _viewState.value.copy(transactionType = type)
 }

 private fun selectDate(date: Date) {
 _viewState.value = _viewState.value.copy(
 transactionDate = date,
 showDatePicker = false
 )
 }

 private fun toggleMoreDetails() {
 _viewState.value = _viewState.value.copy(
 showMoreDetails = !_viewState.value.showMoreDetails
 )
 }

 private fun showCategorySelector() {
 _viewState.value = _viewState.value.copy(showCategories = true)
 }

 private fun showWalletSelector() {
 _viewState.value = _viewState.value.copy(showWallets = true)
 }

 private fun showDatePicker() {
 _viewState.value = _viewState.value.copy(showDatePicker = true)
 }

 private fun handleNumpadPress(button: NumpadButton) {
 when (button) {
 NumpadButton.CLEAR -> clearAmount()
 NumpadButton.DELETE -> deleteLastDigit()
 NumpadButton.DECIMAL -> addDecimal()
 NumpadButton.ZERO, NumpadButton.ONE, NumpadButton.TWO, NumpadButton.THREE,
 NumpadButton.FOUR, NumpadButton.FIVE, NumpadButton.SIX, NumpadButton.SEVEN,
 NumpadButton.EIGHT, NumpadButton.NINE -> addDigit(button.displayText)

 NumpadButton.TRIPLE_ZERO -> addTripleZero()
 NumpadButton.PLUS, NumpadButton.MINUS,
 NumpadButton.MULTIPLY, NumpadButton.DIVIDE -> handleOperator(button)

 NumpadButton.ACTION -> toggleTransactionType()
 NumpadButton.SAVE -> {
 // Save will be handled through the Save intent
 }
 }
 }

 private fun clearAmount() {
 _viewState.value = _viewState.value.copy(amount = "0", isValidAmount = false)
 }

 private fun deleteLastDigit() {
 val currentAmount = _viewState.value.amount
 if (currentAmount.length > 1) {
 val newAmount = currentAmount.substring(0, currentAmount.length - 1)
 _viewState.value = _viewState.value.copy(
 amount = newAmount,
 isValidAmount = newAmount.toDoubleOrNull() != null && newAmount.toDoubleOrNull() != 0.0
 )
 } else {
 _viewState.value = _viewState.value.copy(amount = "0", isValidAmount = false)
 }
 }

 private fun addDigit(digit: String) {
 val currentAmount = _viewState.value.amount
 val newAmount = if (currentAmount == "0") digit else currentAmount + digit

 _viewState.value = _viewState.value.copy(
 amount = newAmount,
 isValidAmount = newAmount.toDoubleOrNull() != null && newAmount.toDoubleOrNull() != 0.0
 )
 }

 private fun addDecimal() {
 val currentAmount = _viewState.value.amount
 if (!currentAmount.contains(".")) {
 _viewState.value = _viewState.value.copy(amount = "$currentAmount.")
 }
 }

 private fun addTripleZero() {
 val currentAmount = _viewState.value.amount
 if (currentAmount != "0") {
 _viewState.value = _viewState.value.copy(
 amount = "$currentAmount${"000"}",
 isValidAmount = true
 )
 }
 }

 private fun handleOperator(button: NumpadButton) {
 // Basic calculator functionality
 // This would need more complex implementation for a real calculator
 // Here we'll just append the operator to prepare for next number
 val currentAmount = _viewState.value.amount
 val operator = when (button) {
 NumpadButton.PLUS -> "+"
 NumpadButton.MINUS -> "-"
 NumpadButton.MULTIPLY -> "×"
 NumpadButton.DIVIDE -> "÷"
 else -> return
 }

 // For this simple implementation, we'll just add the operator if amount doesn't end with one
 if (!currentAmount.endsWith("+") && !currentAmount.endsWith("-") &&
 !currentAmount.endsWith("×") && !currentAmount.endsWith("÷")
 ) {
 _viewState.value = _viewState.value.copy(amount = "$currentAmount$operator")
 }
 }

 private fun toggleTransactionType() {
 val currentType = _viewState.value.transactionType
 val newType = if (currentType == TransactionType.INFLOW) {
 TransactionType.OUTFLOW
 } else {
 TransactionType.INFLOW
 }
 _viewState.value = _viewState.value.copy(transactionType = newType)
 }

 private fun saveTransaction() {
 viewModelScope.launch {
 try {
 _viewState.value = _viewState.value.copy(isSaving = true)

 // Validate transaction data
 val currentState = _viewState.value
 if (!currentState.isValidAmount || !currentState.isValidCategory || !currentState.isValidWallet) {
 _viewState.value = _viewState.value.copy(
 isSaving = false,
 error = IllegalStateException("Please fill in all required fields")
 )
 emitEvent(AddTransactionEvent.ShowError("Please fill in all required fields"))
 return@launch
 }

 // Determine transaction type based on category type
 val transactionType = when (currentState.category!!.type) {
 CategoryType.INCOME -> TransactionType.INFLOW
 CategoryType.EXPENSE -> TransactionType.OUTFLOW
 CategoryType.DEBT_LOAN -> {
 // For debt/loan categories, we need more specific logic
 when (currentState.category.metaData) {
 "IS_DEBT", "IS_LOAN" -> TransactionType.OUTFLOW
 "IS_DEBT_COLLECTION", "IS_REPAYMENT" -> TransactionType.INFLOW
 else -> currentState.transactionType // Keep current if unknown
 }
 }
 CategoryType.UNKNOWN -> currentState.transactionType // Keep current if unknown
 }

 // Log for debugging
 Log.d("Transaction", "Category: ${currentState.category.title}, Type: ${currentState.category.type}")
 Log.d("Transaction", "Determined transaction type: $transactionType")

 // Save transaction using SaveTransactionUseCase
 val params = SaveTransactionUseCase.Params(
 wallet = WalletWithCurrency(
 id = currentState.walletWithCurrency.wallet.id,
 walletName = currentState.walletWithCurrency.wallet.walletName,
 currentBalance = currentState.walletWithCurrency.wallet.currentBalance,
 currency = Currency(
 id = currentState.walletWithCurrency.currency.id,
 currencyName = currentState.walletWithCurrency.currency.currencyName,
 currencyCode = currentState.walletWithCurrency.currency.currencyCode,
 symbol = currentState.walletWithCurrency.currency.symbol,
 displayType = currentState.walletWithCurrency.currency.displayType,
 image = currentState.walletWithCurrency.currency.image
 )
 ),
 amount = currentState.amount.toDouble(),
 transactionType = transactionType,
 transactionDate = currentState.transactionDate,
 description = currentState.description.ifEmpty { null },
 category = currentState.category!!,
 withPerson = currentState.withPerson.ifEmpty { null },
 eventName = currentState.eventName.ifEmpty { null },
 hasReminder = currentState.reminderSet,
 excludeFromReport = currentState.excludeFromReport,
 photoUri = currentState.photoUri
 )

 saveTransactionUseCase(params, viewModelScope) { result ->
 result.fold(
 { failure ->
 _viewState.value = _viewState.value.copy(
 isSaving = false,
 error = Exception(failure.toString())
 )
 emitEvent(AddTransactionEvent.ShowError("Failed to save transaction"))
 },
 { transactionId ->
 _viewState.value = _viewState.value.copy(
 isSaving = false,
 isSuccess = true
 )
 emitEvent(AddTransactionEvent.TransactionSaved)
 emitEvent(AddTransactionEvent.NavigateBack)
 }
 )
 }

 } catch (e: Exception) {
 _viewState.value = _viewState.value.copy(
 isSaving = false,
 error = e
 )
 emitEvent(AddTransactionEvent.ShowError(e.message ?: "An error occurred"))
 }
 }
 }

 private fun updateWithPerson(person: String) {
 _viewState.value = _viewState.value.copy(withPerson = person)
 }

 private fun updateEventName(event: String) {
 _viewState.value = _viewState.value.copy(eventName = event)
 }

 private fun setReminder(hasReminder: Boolean) {
 _viewState.value = _viewState.value.copy(reminderSet = hasReminder)
 }

 private fun updateExcludeFromReport(exclude: Boolean) {
 _viewState.value = _viewState.value.copy(excludeFromReport = exclude)
 }

 private fun updatePhotoUri(uri: String?) {
 _viewState.value = _viewState.value.copy(photoUri = uri)
 }
}
```
