app/src/main/java/com/duongpt/expensetracker/features/money/ui/choosewallet/ChooseWalletViewModel.kt
```kotlin:app/src/main/java/com/duongpt/expensetracker/features/money/ui/choosewallet/ChooseWalletViewModel.kt
package com.duongpt.expensetracker.features.money.ui.choosewallet

import androidx.lifecycle.viewModelScope
import com.duongpt.expensetracker.core.interactor.UseCase
import com.duongpt.expensetracker.core.platform.BaseViewModel
import com.duongpt.expensetracker.features.money.domain.model.Currency
import com.duongpt.expensetracker.features.money.domain.model.WalletWithCurrency
import com.duongpt.expensetracker.features.money.domain.usecases.GetWalletsUseCase
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.flow.launchIn
import kotlinx.coroutines.flow.onEach
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class ChooseWalletViewModel @Inject constructor(
 private val getWalletsUseCase: GetWalletsUseCase
) : BaseViewModel<ChooseWalletState, ChooseWalletIntent, ChooseWalletEvent>() {

 override val _viewState = MutableStateFlow(ChooseWalletState())
 private val TOTAL_WALLET_ID = -1 // Special ID for total wallet

 override fun processIntent(intent: ChooseWalletIntent) {
 when (intent) {
 is ChooseWalletIntent.LoadWallets -> loadWallets()
 is ChooseWalletIntent.SelectWallet -> selectWallet(intent.walletId)
 is ChooseWalletIntent.SelectTotalWallet -> selectTotalWallet()
 is ChooseWalletIntent.ConfirmSelection -> confirmSelection()
 is ChooseWalletIntent.Cancel -> cancelSelection()
 }
 }

 private fun loadWallets() {
 _viewState.value = _viewState.value.copy(isLoading = true)

 getWalletsUseCase(UseCase.None()) { result ->
 result.fold(
 { failure ->
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = failure.toString()
 )
 emitEvent(ChooseWalletEvent.ShowError("Failed to load wallets"))
 },
 { walletsFlow ->
 walletsFlow
 .onEach { walletEntities ->
 // Convert entities to domain models
 val wallets = walletEntities.map { WalletWithCurrency.fromEntity(it) }

 // Create total wallet
 val totalWallet = createTotalWallet(wallets)

 _viewState.value = _viewState.value.copy(
 isLoading = false,
 wallets = wallets,
 totalWallet = totalWallet
 )
 }
 .catch { e ->
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = e.message ?: "Unknown error occurred"
 )
 emitEvent(ChooseWalletEvent.ShowError(e.message ?: "Failed to load wallets"))
 }
 .launchIn(viewModelScope)
 }
 )
 }
 }

 private fun createTotalWallet(wallets: List<WalletWithCurrency>): WalletWithCurrency {
 // Use VND as default currency or your app's base currency
 val defaultCurrency = Currency(
 id = 1, // Use your app's main currency ID
 currencyName = "Vietnamese Dong",
 currencyCode = "VND",
 symbol = "₫",
 displayType = "suffix" 
 )

 // Calculate total balance across all wallets
 // Note: In a real app, you might need currency conversion logic here
 val totalBalance = wallets.sumOf { it.currentBalance }

 return WalletWithCurrency(
 id = TOTAL_WALLET_ID, // Use negative ID to indicate special wallet
 walletName = "All Wallets",
 currentBalance = totalBalance,
 currency = defaultCurrency,
 icon = "img_total_wallet", // Use appropriate icon
 isMainWallet = false
 )
 }

 private fun selectWallet(walletId: Int) {
 _viewState.value = _viewState.value.copy(selectedWalletId = walletId)
 }

 private fun selectTotalWallet() {
 _viewState.value = _viewState.value.copy(selectedWalletId = TOTAL_WALLET_ID)
 }

 private fun confirmSelection() {
 val selectedId = _viewState.value.selectedWalletId ?: return

 if (selectedId == TOTAL_WALLET_ID) {
 // Handle total wallet selection
 val totalWallet = _viewState.value.totalWallet
 totalWallet?.let {
 emitEvent(ChooseWalletEvent.WalletSelected(it, true))
 } ?: emitEvent(ChooseWalletEvent.ShowError("Total wallet not available"))
 return
 }

 // Handle regular wallet selection
 val selectedWallet = _viewState.value.wallets.find { it.id == selectedId }
 selectedWallet?.let {
 emitEvent(ChooseWalletEvent.WalletSelected(it))
 } ?: emitEvent(ChooseWalletEvent.ShowError("No wallet selected"))
 }

 private fun cancelSelection() {
 emitEvent(ChooseWalletEvent.NavigateBack)
 }
} 
