package com.duongpt.expensetracker.features.money.ui.mywallets

import androidx.lifecycle.viewModelScope
import com.duongpt.expensetracker.core.failure.Failure
import com.duongpt.expensetracker.core.functional.Either
import com.duongpt.expensetracker.core.interactor.UseCase
import com.duongpt.expensetracker.core.platform.BaseViewModel
import com.duongpt.expensetracker.features.money.data.data_source.local.model.WalletWithCurrencyEntity
import com.duongpt.expensetracker.features.money.domain.model.WalletWithCurrency
import com.duongpt.expensetracker.features.money.domain.usecases.GetWalletsUseCase
import com.duongpt.expensetracker.features.money.ui.mywallets.MyWalletsEvent
import com.duongpt.expensetracker.features.money.ui.mywallets.MyWalletsIntent
import com.duongpt.expensetracker.features.money.ui.mywallets.MyWalletsState
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.flow.collect
import kotlinx.coroutines.flow.launchIn
import kotlinx.coroutines.flow.onEach
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class MyWalletsViewModel @Inject constructor(
 private val getWalletsUseCase: GetWalletsUseCase
) : BaseViewModel<MyWalletsState, MyWalletsIntent, MyWalletsEvent>() {

 override val _viewState = MutableStateFlow(MyWalletsState())

 init {
 loadWallets()
 }

 override fun processIntent(intent: MyWalletsIntent) {
 when (intent) {
 is MyWalletsIntent.LoadWallets -> loadWallets()
 is MyWalletsIntent.AddWallet -> {
 // Emit event to navigate to add wallet screen with the selected wallet type
 // This will be handled in the UI to navigate to the appropriate screen
 emitEvent(MyWalletsEvent.NavigateToAddWallet(intent.walletType))
 }
 is MyWalletsIntent.DeleteWallet -> {
 // Implementation for deleting wallet would go here
 emitEvent(MyWalletsEvent.WalletDeleted)
 }
 is MyWalletsIntent.SetMainWallet -> {
 // Implementation for setting main wallet would go here
 emitEvent(MyWalletsEvent.MainWalletSet)
 }
 }
 }

 private fun loadWallets() {
 _viewState.value = _viewState.value.copy(isLoading = true)

 viewModelScope.launch {
 getWalletsUseCase(UseCase.None()) { result ->
 result.fold(
 { failure ->
 handleWalletsFailure(failure)
 },
 { walletsFlow ->
 walletsFlow
 .onEach { wallets -> handleWalletsSuccess(wallets) }
 .catch { e -> handleWalletsFailure(Failure.DatabaseError) }
 .launchIn(viewModelScope)
 }
 )
 }
 }
 }

 private fun handleWalletsSuccess(walletsEntities: List<WalletWithCurrencyEntity>) {
 val wallets = walletsEntities.map { WalletWithCurrency.fromEntity(it) }
 val totalBalance = calculateTotalBalance(wallets)

 _viewState.value = _viewState.value.copy(
 isLoading = false,
 wallets = wallets,
 totalBalance = totalBalance,
 error = null
 )
 }

 private fun handleWalletsFailure(failure: Failure) {
 _viewState.value = _viewState.value.copy(
 isLoading = false,
 error = "Failed to load wallets: ${failure.javaClass.simpleName}"
 )

 emitEvent(MyWalletsEvent.ShowError("Failed to load wallets"))
 handleFailure(failure)
 }

 private fun calculateTotalBalance(wallets: List<WalletWithCurrency>): Double {
 // In a real app, you might need currency conversion for accurate total
 return wallets.sumOf { it.currentBalance }
 }
} 
