@Composable
fun AddTransactionScreen(
 viewModel: AddTransactionViewModel = hiltViewModel(),
 onCloseClick: () -> Unit = {},
 navController: NavController = rememberNavController()
) {
 val state by viewModel.viewState.collectAsState()

 // Observe the selected category when returning from CategoryScreen
 LaunchedEffect(Unit) {
 val savedStateHandle = navController.currentBackStackEntry?.savedStateHandle
 savedStateHandle?.get<Category>("selected_category")?.let { category ->
 savedStateHandle.remove<Category>("selected_category")
 viewModel.processIntent(AddTransactionIntent.SelectCategory(category))
 }
 }

 LaunchedEffect(Unit) {
 viewModel.event.collect { event ->
 when (event) {
 AddTransactionEvent.NavigateBack -> {
 navController.navigateUp()
 }
 is AddTransactionEvent.ShowError -> {

 }
 AddTransactionEvent.TransactionSaved -> {

 }
 null -> {}
 }
 }
 }

 Scaffold(
 topBar = {
 AddTransactionTopAppBar(onCloseClick = onCloseClick, title = "Add Transaction")
 },
 containerColor = Color(0xFF1C1C1E)
 ) { paddingValues ->
 AddTransactionContent(
 state = state,
 onIntent = { intent -> viewModel.processIntent(intent) },
 paddingValues = paddingValues,
 onCategoryClick = { navController.navigate(Screen.Category.route) }
 )
 }
}

@Composable
fun AddTransactionContent(
 state: AddTransactionState,
 onIntent: (AddTransactionIntent) -> Unit = {},
 paddingValues: PaddingValues,
 onCategoryClick: () -> Unit = {}
) {
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 ) {
 // Scrollable input fields area and suggestions take available vertical space
 Column(
 modifier = Modifier.weight(1f)
 ) {
 TransactionInputFields(
 amount = state.amount,
 walletName = state.walletWithCurrency.wallet.walletName,
 currency = state.walletWithCurrency.currency.currencyCode,
 category = state.category,
 description = state.description,
 transactionDate = state.transactionDate,
 showMoreDetails = state.showMoreDetails,
 withPerson = state.withPerson,
 eventName = state.eventName,
 reminderSet = state.reminderSet,
 excludeFromReport = state.excludeFromReport,
 onDescriptionChange = { onIntent(AddTransactionIntent.UpdateDescription(it)) },
 onToggleMoreDetails = { onIntent(AddTransactionIntent.ToggleMoreDetails) },
 onWalletClick = { onIntent(AddTransactionIntent.ShowWalletSelector) },
 onCategoryClick = {
 onIntent(AddTransactionIntent.ShowCategorySelector)
 onCategoryClick()
 },
 onDateClick = { onIntent(AddTransactionIntent.ShowDatePicker) },
 onWithPersonClick = { /* Open person selector */ },
 onEventClick = { /* Open event selector */ },
 onReminderClick = { onIntent(AddTransactionIntent.SetReminder(!state.reminderSet)) },
 onExcludeFromReportChange = {
 onIntent(
 AddTransactionIntent.UpdateExcludeFromReport(
 it
 )
 )
 },
 onTakePhotoClick = { /* Handle photo capture */ },
 onGalleryClick = { /* Handle gallery selection */ }
 )

 AmountSuggestionBar(
 suggestedAmounts = state.suggestedAmounts,
 onSuggestionClick = { onIntent(AddTransactionIntent.UpdateAmount(it)) }
 )
 }

 // Numpad is fixed at bottom of screen
 TransactionNumpad(
 onButtonClick = { button ->
 onIntent(AddTransactionIntent.NumpadPressed(button))
 if (button == NumpadButton.SAVE) {
 onIntent(AddTransactionIntent.Save)
 }
 }
 )
 }
}

