/**
 * Budget Screen UI
 */
@Composable
fun BudgetScreen(
 modifier: Modifier = Modifier,
 viewModel: BudgetViewModel = hiltViewModel(),
 navController: NavController,
 onCreateBudgetClick: () -> Unit = {},
 onHowToUseClick: () -> Unit = {}
) {
 // Collect view state
 val viewState by viewModel.viewState.collectAsState()

 // Handle wallet selection from ChooseWalletScreen
 LaunchedEffect(navController) {
 val savedStateHandle = navController.currentBackStackEntry?.savedStateHandle

 // Get wallet selection from savedStateHandle
 savedStateHandle?.get<Int>("selected_wallet_id")?.let { walletId ->
 val isTotalWallet = savedStateHandle.get<Boolean>("is_total_wallet") ?: false

 // Process the selected wallet
 viewModel.processIntent(BudgetScreenIntent.ChangeWallet(walletId, isTotalWallet))

 // Clear the saved state to avoid processing it again if we return to this screen
 savedStateHandle.remove<Int>("selected_wallet_id")
 savedStateHandle.remove<Boolean>("is_total_wallet")
 }
 }

 // Handle events
 val lifecycleOwner = LocalLifecycleOwner.current
 LaunchedEffect(key1 = viewModel, key2 = lifecycleOwner) {
 viewModel.event
 .flowWithLifecycle(lifecycleOwner.lifecycle, Lifecycle.State.STARTED)
 .collectLatest { event ->
 when (event) {
 is BudgetScreenEvent.NavigateToCreateBudget -> {
 navController.navigate(Screen.AddBudget.route)
 }
 is BudgetScreenEvent.ShowError -> {
 // Show error toast or snackbar
 }
 is BudgetScreenEvent.WalletChanged -> {
 // Handle wallet changed event if needed
 }
 is BudgetScreenEvent.BudgetCreated -> {
 // Handle budget created event if needed
 }
 is BudgetScreenEvent.BudgetDeleted -> {
 // Handle budget deleted event if needed
 }
 null -> { /* Ignore null events */ }
 }
 }
 }

 // Initial data load
 LaunchedEffect(key1 = Unit) {
 viewModel.processIntent(BudgetScreenIntent.LoadBudgets)
 }

 // Render the screen
 BudgetScreenContent(
 state = viewState,
 onWalletSelectorClick = {
 // Navigate to ChooseWalletScreen using the route helper
 navController.navigate(Screen.ChooseWallet.createRoute())
 },
 onCreateBudgetClick = {
 viewModel.processIntent(BudgetScreenIntent.CreateBudget)
 },
 onRefreshBudgets = {
 viewModel.processIntent(BudgetScreenIntent.RefreshBudgets)
 },
 onDeleteBudget = { budgetId ->
 viewModel.processIntent(BudgetScreenIntent.DeleteBudget(budgetId))
 },
 onHowToUseClick = onHowToUseClick
 )
}

