 onHowToUseClick: () -> Unit
) {
 Scaffold(
 topBar = {
 TopAppBar(
 title = { 
 Column {
 Text("Running Budgets", color = Color.White)

 // Display current wallet name
 state.currentWallet?.let { wallet ->
 Text(
 text = if (state.isTotalWallet) "All Wallets" else wallet.walletName,
 color = Color.White.copy(alpha = 0.7f),
 fontSize = 12.sp
 )
 }
 }
 },
 colors = TopAppBarDefaults.topAppBarColors(
